// 柱形图
const BarChart = (props: {
  title: string
  value: {
    x: string
    y: number
  }[]
  yLabel?: string 
  xLabel?: string 
} & Pick<CommonAnimationProps, 'onComplete'>) => {

  const { title, value, onComplete, yLabel='', xLabel='' } = props

  const chartRef = useRef<SVGSVGElement>(null)

  const realData = useMemo(() => {
    let labels: string[] = []
    let values: number[] = []
    value.forEach(item => {
      const { x, y } = item
      labels.push(x)
      values.push(y)
    })
    return {
      labels,
      datasets: [{
        data: values,
      }]
    }
  }, [value])

  useChartInit(() => {
    new ChartXkcd.Bar(chartRef.current, {
      title,
      yLabel,
      xLabel,
      data: realData,
      options: {
        backgroundColor: 'transparent',
        dataColors: COLOR_LIST
      }
    })
  }, onComplete, [title, realData])

  return (
    <div
      className="home-page-chart-wrapper"
    >
      <svg
        ref={chartRef}
        className="home-page-bar-chart"
      >

      </svg>
    </div>
  )

}

